mapper.insert:

INSERT INTO EMI_BRANCH_INFO (
	branch_id
	, branch_nm
	, branch_parent_id
	, use_yn
	, remark
	, reg_id
	, reg_dt
	, update_id
	, update_dt
) VALUES (
	  #{branch_id}
	, #{branch_nm}
	, #{branch_parent_id}
	, #{use_yn}
	, #{remark}
	, #{reg_id}
	, now()
	, #{update_id}
	, now()			
)

mapper.update:
UPDATE EMI_BRANCH_INFO SET			
	branch_nm = #{branch_nm}
	, branch_parent_id = #{branch_parent_id}
	, use_yn = #{use_yn}
	, remark = #{remark}			
	, update_id = #{update_id}
	, update_dt = now()
WHERE branch_id = #{branch_id}
