mapper.insert:

	SELECT CONCAT('D', SUBSTR(IFNULL(SUBSTR(MAX(device_id),2),0) + 1000000001,2)) AS device_id
	FROM EMI_DEVICE_INFO				


INSERT INTO EMI_DEVICE_INFO (
	  device_id
	, device_serial
	, service_id
	, branch_id
	, model_seq
	, device_oid
	, comm_type
	, device_type
	, ip
	, port
	, allow_yn			
	, reg_id
	, reg_dt
	, dest_dt
) VALUES (
	  #{device_id}
	, #{device_serial}
	, #{service_id}
	, #{branch_id}
	, #{model_seq}
	, #{device_oid}
	, #{comm_type}
	, #{device_type}
	, INET_ATON(#{ip})
	, #{port}
	, #{allow_yn}			
	, #{reg_id}
	, now()			
)

mapper.update:
UPDATE EMI_DEVICE_INFO SET
	device_serial = #{device_serial}
	, service_id = #{service_id}
	, branch_id = #{branch_id}
	, model_seq = #{model_seq}
	, device_oid = #{device_oid}
	, comm_type = #{comm_type}
	, device_type = #{device_type}
	, ip = INET_ATON(#{ip})
	, port = #{port}
	, allow_yn = #{allow_yn}			
WHERE device_id = #{device_id}
