mapper.insert:

INSERT INTO EMI_DEVICE_RESOURCE (
	device_id
	, resource_path
	, object_id
	, object_instance_id
	, resource_id
	, resource_instance_id
	, resource_val
	, reg_id
	, reg_dt
	, update_id
	, update_dt
) VALUES (
	#{device_id}
	, #{resource_path}
	, #{object_id}
	, #{object_instance_id}
	, #{resource_id}
	, #{resource_instance_id}
	, #{resource_val}
	, #{reg_id}
	, now()
	, #{update_id}
	, now()
)


INSERT INTO EMI_DEVICE_RESOURCE (
	device_id
	, resource_path
	, object_id
	, object_instance_id
	, resource_id
	, resource_instance_id
	, resource_val
	, reg_id
	, reg_dt
	, update_id
	, update_dt
) 
VALUES 

	#{list.device_id}
	, #{list.resource_path}
	, #{list.object_id}
	, #{list.object_instance_id}
	, #{list.resource_id}
	, #{list.resource_instance_id}
	, #{list.resource_val}
	, #{list.reg_id}			
	, now()
	, #{list.update_id}
	, now()

ON DUPLICATE KEY UPDATE resource_val=VALUES(resource_val),update_dt=now();

mapper.delete:
DELETE FROM 
	EMI_DEVICE_RESOURCE 
WHERE device_id = #{device_id} and resource_path = #{resource_path}

mapper.update:
UPDATE EMI_DEVICE_RESOURCE SET			
	object_id = #{object_id}
	, object_instance_id = #{object_instance_id}
	, resource_id = #{resource_id}
	, resource_instance_id = #{resource_instance_id}
	, resource_val = #{resource_val}			
	, update_id = #{update_id}
	, update_dt = now()
WHERE device_id = #{device_id} and resource_path = #{resource_path}

mapper.select:
SELECT a.device_id , c.object_nm , a.object_id , a.object_instance_id , c.instances , b.descr FROM EMI_DEVICE_RESOURCE a INNER JOIN EMI_RESOURCE_MODEL b ON a.object_id = b.object_id AND a.resource_id = b.resource_id INNER JOIN EMI_OBJECT_MODEL c ON a.object_id = c.object_id
AND a.device_id = #{device_id}
AND c.object_nm = #{object_nm}
GROUP BY a.device_id, a.object_id

SELECT a.device_id , a.object_id , c.object_nm , a.object_instance_id , a.resource_id , b.resource_nm , a.resource_instance_id , b.operation , b.instances , b.mandatory , a.resource_val , b.unit FROM EMI_DEVICE_RESOURCE a INNER JOIN EMI_RESOURCE_MODEL b ON ( a.object_id = b.object_id AND a.resource_id = b.resource_id ) INNER JOIN EMI_OBJECT_MODEL c ON ( a.object_id = c.object_id )
AND a.device_id = #{device_id}
AND a.object_id = #{object_id}
