mapper.insert:
     	
		INSERT INTO EMI_DEVICE_STATUS (
			device_id
			, device_flag
			, device_status
			, last_comm_dt
			, last_renw_dt
		) VALUES (
			#{device_id}
			, #{device_flag}
			, #{device_status}
			, DATE_FORMAT(#{last_comm_dt},'${prop.dt}')
			, now()	
		)

mapper.update:
UPDATE EMI_DEVICE_STATUS SET
	device_flag = #{device_flag}
	, device_status = #{device_status}
	, last_comm_dt = DATE_FORMAT(#{last_comm_dt},'${prop.dt}')
	, last_renw_dt = now()			
WHERE device_id = #{device_id}
