mapper.insert:


	SELECT CONCAT('MT', SUBSTR(IFNULL(SUBSTR(MAX(meter_id),3),0) + 100000001,2)) AS meter_id 
	FROM EMI_METER_INFO


INSERT INTO EMI_METER_INFO (
	  meter_id
	, meter_serial
	, device_id
	, meter_type
	, energy_type_code
	, meter_phase
	, branch_id
	, model_seq
	, ip
	, port
	, prog_id
	, prog_version
	, latitude
	, longtitude
	, altitude
	, cosem_device_name
	, acon
	, rcon
	, billing_dt
	, itime
	, mtime
	, lp_period
	, pcon
	, net_metering
	, reg_dt
) VALUES (
	  #{meter_id}
	, #{meter_serial}
	, #{device_id}
	, #{meter_type}
	, #{energy_type_code}
	, #{meter_phase}
	, #{branch_id}
	, #{model_seq}
	, INET_ATON(#{ip})
	, #{port}
	, #{prog_id}
	, #{prog_version}
	, #{latitude}
	, #{longtitude}
	, #{altitude}
	, #{cosem_device_name}
	, #{acon}
	, #{rcon}
	, #{billing_dt}
	, #{itime}
	, #{mtime}
	, #{lp_period}
	, #{pcon}
	, #{net_metering}
	, now()
)

mapper.update:
UPDATE EMI_METER_INFO SET			 
	meter_serial = #{meter_serial}
	 , device_id = #{device_id} 
	, meter_type = #{meter_type}
	, energy_type_code = #{energy_type_code}
	, meter_phase = #{meter_phase}
	, branch_id = #{branch_id}
	, model_seq = #{model_seq}
	, ip = INET_ATON(#{ip})
	, port = #{port}
	, prog_id = #{prog_id}
	, prog_version = #{prog_version}
	, latitude = #{latitude}
	, longtitude = #{longtitude}
	, altitude = #{altitude}	
	, cosem_device_name = #{cosem_device_name}
	, acon = #{acon}
	, rcon = #{rcon}
	, billing_dt = #{billing_dt}
	, itime = #{itime}
	, mtime = #{mtime}
	, lp_period = #{lp_period}
	, pcon = #{pcon}
	, net_metering = #{net_metering}
WHERE meter_id = #{meter_id}
