mapper.insert:

INSERT INTO EMI_METER_VALUE (
	meter_id
	, read_dt
	, channel
	, meter_value
	, sect_meter_value
) VALUES (
	  #{meter_id}
	, #{read_dt}
	, #{channel}
	, #{meter_value}
	, #{sect_meter_value}
)

INSERT INTO EMI_METER_VALUE (
	meter_id
	, read_dt
	, channel
	, meter_value
	, sect_meter_value
	, reg_dt	
) VALUES	

	#{list.meter_id}
	, #{list.read_dt}
	, #{list.channel}
	, #{list.meter_value}
	, #{list.sect_meter_value}			
	, now()

ON DUPLICATE KEY UPDATE meter_value=VALUES(meter_value);

mapper.update:
	UPDATE EMI_METER_VALUE SET			
		meter_value = #{meter_value}
		, sect_meter_value = #{sect_meter_value}
	WHERE meter_id = #{meter_id} 
      AND read_dt = #{read_dt} 
      AND channel = #{channel}
