mapper.insert:

INSERT INTO EMI_OPERATION_LOG (
	device_id
	, request_dt
	, service_id
	, method
	, object_id
	, object_instance_id
	, resource_id
	, resource_instance_id
	, format
	, token
	, mid
	, payload
	, result
	, result_dt
	, reg_id
) VALUES (
	  #{device_id}
	, #{request_dt}
	, #{service_id}
	, #{method}
	, #{object_id}
	, #{object_instance_id}
	, #{resource_id}
	, #{resource_instance_id}
	, #{format}
	, #{token}
	, #{mid}
	, #{payload}
	, #{result}
	, #{result_dt}
	, #{reg_id}
)

mapper.update:
UPDATE EMI_OPERATION_LOG SET
	service_id = #{service_id}
	, method = #{method}
	, object_id = #{object_id}
	, object_instance_id = #{object_instance_id}
	, resource_id = #{resource_id}
	, resource_instance_id = #{resource_instance_id}
	, format = #{format}
	, token = #{token}
	, mid = #{mid}
	, payload = #{payload}
	, result = #{result}
	, result_dt = #{result_dt}
	, reg_id = #{reg_id}
WHERE device_id = #{device_id} and request_dt = #{request_dt}
