mapper.insert:

INSERT INTO EMI_RESOURCE_MODEL (
	object_id
	, resource_id
	, resource_nm
	, operation
	, instances
	, mandatory
	, data_type
	, range
	, unit
	, descr
	, reg_id
	, reg_dt
	, update_id
	, update_dt	
) VALUES (
	 #{object_id}
	, #{resource_id}
	, #{resource_nm}
	, #{operation}
	, #{instances}
	, #{mandatory}
	, #{data_type}
	, #{range}
	, #{unit}
	, #{descr}
	, #{reg_id}
	, now()
	, #{update_id}
	, now()
)

mapper.update:
UPDATE EMI_RESOURCE_MODEL SET			
	resource_nm = #{resource_nm}
	, operation = #{operation}
	, instances = #{instances}
	, mandatory = #{mandatory}
	, data_type = #{data_type}
	, range = #{range}
	, unit = #{unit}
	, descr = #{descr}			
	, update_id = #{update_id}
	, update_dt = now()	
WHERE 
	object_id = #{object_id}
AND resource_id = #{resource_id}
