	, code_nm
	, code_local_nm
	, parent_code
	, sort
	, use_yn
	, remark
	, reg_id
	, reg_dt
	, update_id
	, update_dt
	, add_col1
	, add_col2
	, add_col3
	, add_col4
	, add_col5
) VALUES (
	  #{code}
	, #{code_nm}
	, #{code_local_nm}
	, #{parent_code}
	, #{sort}
	, #{use_yn}
	, #{remark}
	, #{reg_id}
	, now()
	, #{update_id}
	, now()
	, #{add_col1}
	, #{add_col2}
	, #{add_col3}
	, #{add_col4}
	, #{add_col5}
)

mapper.update:
UPDATE EMI_ALL_CODE SET			
	code_nm = #{code}
	, code_local_nm = #{code}
	, parent_code = #{code}
	, sort = #{code}
	, use_yn = #{code}
	, remark = #{code}
	, update_id = #{code}
	, update_dt = now()
	, add_col1 = #{code}
	, add_col2 = #{code}
	, add_col3 = #{code}
	, add_col4 = #{code}
	, add_col5 = #{code}
WHERE code = #{code}
