mapper.insert:

INSERT INTO EMI_METER_INFO (
	  meter_id
	, meter_serial
	, device_id
	, meter_type
	, energy_type_code
	, meter_phase
	, branch_id
	, model_seq
	, ip
	, port
	, prog_id
	, prog_version
	, latitude
	, longtitude
	, altitude
	, reg_dt
) VALUES (
	  #{meter_id}
	, #{meter_serial}
	, #{device_id}
	, #{meter_type}
	, #{energy_type_code}
	, #{meter_phase}
	, #{branch_id}
	, #{model_seq}
	, INET_ATON(#{ip})
	, #{port}
	, #{prog_id}
	, #{prog_version}
	, #{latitude}
	, #{longtitude}
	, #{altitude}
	, now()
)

mapper.update:
UPDATE EMI_METER_INFO SET			 
	meter_serial = #{meter_serial}
	, device_id = #{device_id}
	, meter_type = #{meter_type}
	, energy_type_code = #{energy_type_code}
	, meter_phase = #{meter_phase}
	, branch_id = #{branch_id}
	, model_seq = #{model_seq}
	, ip = INET_ATON(#{ip})
	, port = #{port}
	, prog_id = #{prog_id}
	, prog_version = #{prog_version}
	, latitude = #{latitude}
	, longtitude = #{longtitude}
	, altitude = #{altitude}			
WHERE meter_id = #{meter_id}
