mapper.insert:

INSERT INTO EMI_VENDOR_INFO (
	vendor_seq
	, vendor_code
	, vendor_nm
	, remark
	, reg_id
	, reg_dt
	, update_id
	, update_dt
) VALUES (
	  #{vendor_seq}
	, #{vendor_code}
	, #{vendor_nm}
	, #{remark}
	, #{reg_id}
	, now()
	, #{update_id}
	, now()
)

mapper.update:
UPDATE EMI_VENDOR_INFO SET			
	vendor_code = #{vendor_code}
	, vendor_nm = #{vendor_nm}
	, remark = #{remark}
	, reg_id = #{reg_id}
	, reg_dt = now()
	, update_id = #{update_id}
	, update_dt = now()
WHERE vendor_seq = #{vendor_seq}
